import { AuthContext } from '../../context/AuthContext'

export function SavedUserArticles() {
    const { user } = useContext(AuthContext)
    const [sectionOpened, setOpened] = useState(false);

    return (
        <div className='saved_articles_wrapper'>
            <button onClick={()=>setOpened(!sectionOpened)} className='open_saved_articles_btn'>Saved Articles <i class=" fa-arrow-down"></i></button>
            <div style={{display: sectionOpened? "block":"none"}}>
                     kks
            </div>
        </div>
    )
}
