import React, { useContext, useState } from 'react'
import './Header.css'
import { NavLink, useNavigate } from 'react-router-dom'
import { AuthContext } from '../../context/AuthContext';
import 'font-awesome/css/font-awesome.min.css';

export function Header() {
    const { user } = useContext(AuthContext);
    const [searchValue, setSearchValue] = useState("");
    const navigate = useNavigate()

    const handleSearch = () => {
        if (!searchValue) {
            navigate(`/search?query=${encodeURIComponent(searchValue)}`);
        }
    }
    return (
        <div className='header_wrapper'>
            <div className='header_firstline'>
                <h1 className='header_title'>
                    Thematic<br /> Portal
                </h1>
                <div className='search_account_div'>
                    <input className='search_input' placeholder='Search...'
                        value={searchValue} onChange={e => setSearchValue(e.target.value)}
                        onKeyDown={(e) => {
                            if (e.key === "Enter") handleSearch();
                        }} />
                    <button onClick={handleSearch}><i className="fa fa-search" style={{ fontSize: '20px', borderBottom: '1px solid  #32323267', paddingBottom: '3px', marginRight: '10px' }}></i></button>

                    {user ? <NavLink to={'/account'} className={'user_header_avatar'}><img src={user?.avatar} /></NavLink> : <div className='autorization_btns'>
                        <NavLink to={'/login'} >Log in</NavLink>
                        <NavLink to={'/register'} >Sign up</NavLink>
                    </div>}
                </div>
            </div>
            <div className='header_menu'>
                <NavLink to={'/'} className={'header_link'}>Home</NavLink>
                <NavLink to={'/'} className={'header_link'}>Trending</NavLink>
                <NavLink to={'/news'} className={'header_link'}>News</NavLink>
                <NavLink to={'/about'} className={'header_link'}>About Us</NavLink>
            </div>
        </div>
    )
}
