import React, { useEffect, useState } from 'react'
import './ArticleCard.css'
import { useNavigate } from 'react-router-dom'

export function ArticleCard({article}) {
    const navigate = useNavigate();

    const getArticleAbstract = async (articleId) => {
        const response = await fetch(`https://www.ebi.ac.uk/europepmc/webservices/rest/annotations?articleId=${articleId}`);
        /*const xmlText = await response.text();
        const parser = new DOMParser();
        const xmlDoc = parser.parseFromString(xmlText, "application/xml");
        
        const abstractText = xmlDoc.querySelector("AbstractText")?.textContent || "Аннотация недоступна";*/
        const data = response.json();
        console.log(data)
        //return abstractText;
    };
    const [articleData, setData] = useState({ })

    useEffect(() => {
        console.log(article)
        const fetchArticleData = async () => {
            const annotation = await getArticleAbstract(article.pmid);
           //annotation: annotation,
           //uid: article.uid
            setData({
                title: article.title,
              
                src: './assets/article.png',
                
            });
        };
    
        if (article.id) { 
            fetchArticleData();
        }
    }, [article]); 
// <p>{articleData?.annotation}</p>
    return (
        <div className='article_card_wrapper' onClick={()=>{ navigate(`/article/${articleData.id}`)}}>
            <div className='article_card_img_div'><img alt={articleData?.title +'_img'} src={articleData?.src}/></div>
            <div className='article_info'>
                <h1>{articleData?.title}</h1>
               
            </div>
        </div>
    )
}
