import React, { createContext, useState } from 'react';
export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);

  const login = (username, pass) => {
    const usersData = localStorage.getItem("users");
    const users = usersData ? JSON.parse(usersData) : null;

    if (!users) {
      alert("No registered users found. Please check your username or password.")
      return;
    }
    if (users.some(user => String(user.password) === String(pass) && user.name === username))
      alert("entrance")
    else {
      alert("Invalid username or password. Please try again.")
    }
  }

  const checkNameUniqueness = (username) => {
    const usersData = localStorage.getItem("users");
    const users = usersData ? JSON.parse(usersData) : null;
    if (!users)
      return true;

    return !users.some(user => user.name === username)
  }
  const register = (data) => {
    const usersData = localStorage.getItem("users");
    const users = usersData ? JSON.parse(usersData) : []
    const newUsers = users.push(data)
    localStorage.setItem("users", JSON.stringify(newUsers))
  };

  const logout = () => {
    alert('logout')
  };

  return (
    <AuthContext.Provider value={{ user, login, logout, register, checkNameUniqueness }}>
      {children}
    </AuthContext.Provider>
  )
}
