import React, { useRef, useState } from 'react'
import './Comments.css'

function Comment({ data }) {
    return (
        <div className='comment_wrapper'>
            <div className='com_avatar'><img alt='avatar' src={data?.src} /></div>
            <div className='com_text_wrapper'>
                <p className='com_username'>{data?.username}</p>
                <p className='com_text'>{data?.text}</p>
            </div>
        </div>
    )
}

function AddCommentForm() {
    const textareaRef = useRef(null);
    const [myComment, setMyComment] = useState("")

    const handleChange = (e) => {
        setMyComment(e.target.value);
        if (textareaRef.current) {
            textareaRef.current.style.height = "70px";
            textareaRef.current.style.height = `${textareaRef.current.scrollHeight}px`
        }

    }
    return (
        <div className='add_comment_form'>
            <p className='add_comment_txt'>Add your own comment:</p>
            <div className='textarea_div'>
                <textarea className='comment_textarea' value={myComment} ref={textareaRef} onChange={(e) => handleChange(e)} maxLength="500" />
                <div className='com_symbols_counter'>{`${myComment.length}/500`}</div>
            </div>
            <button className='comment_btn'>Comment</button>
        </div>
    )
}

export default function Comments() {
    const [comments, setComments] = useState([
        { username: 'Paul', src: './assets/main_banner.jpg', text: 'Nice article!' },
        { username: 'Paul', src: './assets/main_banner.jpg', text: 'Nice article! Add more? i want to read' }])

    return (
        <div className='comments_wrapper'>
            <AddCommentForm />
            <p className='comments_section_txt'>Comments:</p>
            {comments.length > 0 && comments.map((comment, index) => <Comment data={comment} key={'comment' + index} />)}

        </div>
    )
}
