import React, { useState } from 'react'
import './ArticleCard.css'
import { useNavigate } from 'react-router-dom'

export function ArticleCard({article}) {
    const navigate = useNavigate();
    const [articleData, setData] = useState({ descr: 'Ipsum loren lalala ipsum jks jkhsd jsjkan jnhjmdjd ksnhbkdk', src: './assets/registration_banner.png'})

    return (
        <div className='article_card_wrapper' onClick={()=>{ navigate('/article')}}>
            <div className='article_card_img_div'><img alt={articleData.title +'_img'} src={articleData.src}/></div>
            <div className='article_info'>
                <h1>{article.title}</h1>
                <p>{articleData.descr}</p>
            </div>
        </div>
    )
}
