import { useContext, useState } from "react";
import './StarRating.css'
import { AuthContext } from "../../context/AuthContext";
export function StarRating({ articleId }) {
    const {user} = useContext(AuthContext);
    const savedArticle = localStorage.getItem(`article_${articleId}`);

    const currentUser = localStorage.getItem("currentUser")
    //const [user, setUser] = useState(currentUser ? JSON.parse(currentUser) : null)
    const [rating, setRating] = useState(savedArticle ? JSON.parse(savedArticle).rating : 0);
    const [hovered, setHovered] = useState(0);

    const handleMouseEnter = (index) => {
        setHovered(index + 1)
    }
    const handleMouseLeave = () => {
        setHovered(0)
    }
    const handleMouseClick = (index) => {
        if (!user) {
            alert("Enter to rate article")
            return
        }
        const newRating = index + 1;
        setRating(newRating);

        if (savedArticle) {
            localStorage.setItem(`article_${articleId}`, JSON.stringify({ ...JSON.parse(savedArticle), rating: newRating }));
            return
        }
        localStorage.setItem(`article_${articleId}`, JSON.stringify({ rating: newRating, comments: [] }))

    }

    return (
        <div className='star_rating_wrapper'>
            <p className='rating_txt'>Rate this article:</p>
            <div className='stars_div'>
                {[...Array(5)].map((_, index) =>
                    <span
                        key={index}
                        onMouseEnter={() => handleMouseEnter(index)}
                        onMouseLeave={handleMouseLeave}
                        onClick={() => handleMouseClick(index)}
                        style={{
                            fontSize: '35px',
                            cursor: 'pointer',
                            color: (index < hovered || index < rating) ? "#198754" : " #c4c1c1"
                        }}
                    >★</span>)}</div>
        </div>
    )
}
