import React, { useEffect, useState } from 'react'
import './NewsPage.css'
import { ArticleCard } from '../../components/ArticleCard/ArticleCard';

export function NewsPage() {
    const [articlesData, setArticlesData] = useState([])
    const [isLoading, setLoading] = useState(false);
    const [category, setCategory] = useState("");
    const [sortOption, setSortOption] = useState("pub date");
    const [currentPage, setCurrentPage] = useState(1);
    const resultsPerPage = 5;

    useEffect(() => {
        setCategory(JSON.parse(localStorage.getItem("category") || ""))
    }, []);

    const getNewsArticles = async () => {
        try {
            setLoading(true);
            const baseURL = "https://eutils.ncbi.nlm.nih.gov/entrez/eutils/esearch.fcgi";
            const retstart = (currentPage - 1) * resultsPerPage;
            let response;
            response = await fetch(`${baseURL}?db=pubmed&term=${category}[Mesh]+AND+HasAbstract[Filter]&retstart=${retstart}&retmax=${resultsPerPage}&retmode=json`);
            const data = await response.json();
            const idArray = data?.esearchresult?.idlist

            if (idArray && idArray.length > 0) {
                response = await fetch(`https://eutils.ncbi.nlm.nih.gov/entrez/eutils/esummary.fcgi?db=pubmed&id=${idArray.join(',')}&retmode=json`);
                const articlesData = await response.json();
                const uids = articlesData?.result?.uids;
                if (uids && uids.length > 0) {
                    const articles = uids.map(id => {
                        const { title, uid } = articlesData.result[id];
                        const object = { title, uid };
                        return object
                    });
                    setArticlesData(articles);
                }
            }
            else console.log('No articles found');
            setLoading(false)
        } catch (error) {
            console.error('Error fetching articles:', error);
        }
    }

    useEffect(() => {
        getNewsArticles()
    }, [category])

    return (
        <div className='page_wrapper'>
            <div>
                <select value={category} onChange={e => setCategory(e.target.value)}>
                    <option disabled value={""} hidden>Category</option>
                    <option value={"phobia"}>Phobia</option>
                    <option value={"depression"}>Depression</option>
                    <option value={"PTSD"}>PTSD</option>
                    <option value={"mental health"}>Mental health</option>
                    <option value={"anxiety"}>Anxiety</option>
                    <option value={"schizophrenia"}>Schizophrenia</option>
                </select>
            </div>
            <div className='news_page_articles'>
                {(articlesData.length > 0 && !isLoading) && articlesData.map((article, index) => <ArticleCard article={article} key={'article' + index} />)}
                {isLoading && <div className='loading_div'><div className="spinner-border text-success" role="status" >
                    <span className="visually-hidden">Загрузка...</span></div></div>}
            </div>
           
                <ul >
                    <li>{currentPage}</li>
                    <li  onClick={setCurrentPage(currentPage+1)}>{currentPage+1}</li>
                    <li onClick={setCurrentPage(currentPage+2)}>{currentPage+2}</li>
                </ul>
       
        </div>
    )
}
