import React, { createContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
export const AuthContext = createContext();


export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(()=>{
    const user = localStorage.getItem("currentUser");
    if(user) setUser(JSON.parse(user))
  }, [])

  const loadUserFromStorage = () => {
    try {
      const userData = localStorage.getItem("currentUser");
      return userData ? JSON.parse(userData) : null;
    } catch (error) {
      console.error("Error loading user from localStorage:", error);
      return null;
    }
  };

  const login = (username, pass) => {
    const users = JSON.parse(localStorage.getItem("users")||"[]");

    if (users.length === 0) {
      alert("No registered users found. Please check your username or password.")
      return;
    }
    const thisUser = users.find(user => String(user.password) === String(pass) && user.name === username)
    if (thisUser) {
      setUser(thisUser);
      localStorage.setItem("currentUser", JSON.stringify(thisUser));
      navigate('/account');
      alert("Welcome, " + username + "!");
    }

    else {
      alert("Invalid username or password. Please try again.")
    }
  }

  const checkNameUniqueness = (username) => {
    const users = JSON.parse(localStorage.getItem("users") || "[]");
    return !users.some(user => user.name === username);
  }

  const register = (data) => {
    const users = JSON.parse(localStorage.getItem("users")||"[]"); 
    users.push(data)

    localStorage.setItem("users", JSON.stringify(users));
    navigate('/login');
  };

  const logout = () => {
    const users = JSON.parse(localStorage.getItem("users"));
    setUser(null);
    localStorage.removeItem("currentUser");
    navigate("/")
  };

  const saveArticle=({uid, title})=>{
    if(!user) return 
    const updatedUser = {
      ...user,
      savedArticles: [...(user.savedArticles || []), {uid, title} ],
    };
    setUser(updatedUser);
    localStorage.setItem("currentUser", JSON.stringify(updatedUser));
  }

  const deleteSavedArticle = ({uid}) =>{
    if(!user)return;

    const updatedUser = {
      ...user,
      savedArticles: user.savedArticles.filter(article=> String(article.uid) !== String(uid)),
    };
    setUser(updatedUser);
    localStorage.setItem("currentUser", JSON.stringify(updatedUser));
  }

  const rateArticle=(uid, rating)=>{
    if(!user) return;
    const newRatedArticles = user?.ratedArticles?.filter(article => article.uid !== uid) || [];
    const updatedUser = {...user, ratedArticles: [...newRatedArticles, {uid, rating}]}
    setUser(updatedUser);
    localStorage.setItem("currentUser", JSON.stringify(updatedUser));
  }

  const updateAvatar =(newAvatar)=>{
    if(!user) return;

    const updatedUser ={...user, avatar: newAvatar};
    setUser(updatedUser);
    localStorage.setItem("currentUser", JSON.stringify(updatedUser));

  }
  return (
    <AuthContext.Provider value={{ user, 
      login, logout, register, 
    checkNameUniqueness, saveArticle, deleteSavedArticle, 
    updateAvatar, rateArticle }}>
      {children}
    </AuthContext.Provider>
  )
}
