import React, { useEffect, useState } from 'react'
import './ArticlePage.css'
import { useParams } from 'react-router-dom'

export function ArticlePage() {
  const { uid } = useParams();
  const [data, setData] = useState(null)

  const getAllInfo = async () => {
    try {
      const response = await fetch(`https://eutils.ncbi.nlm.nih.gov/entrez/eutils/efetch.fcgi?db=pubmed&id=${uid}&retmode=xml`)
      const xmlText = await response.text();
      const parser = new DOMParser();
      const xmlDoc = parser.parseFromString(xmlText, "application/xml");
      const meshTerms = Array.from(xmlDoc.querySelectorAll("MeshHeading > DescriptorName"))
        .map(term => term.textContent)
         || "No MeSH terms found";
      const title = xmlDoc.querySelector("ArticleTitle")?.textContent || "No title found";
      const abstract = xmlDoc.querySelector("AbstractText")?.textContent || "No abstract available";
      const authors = Array.from(xmlDoc.querySelectorAll("Author > LastName"))
        .map(author => {
          const lastName = author.querySelector("LastName")?.textContent || '';
          const firstName = author.querySelector("FirstName")?.textContent || '';
          return `${firstName} ${lastName}`.trim();})
        .join(", ") || "No authors found";
      const pubDate = xmlDoc.querySelector("PubDate > Year")?.textContent || "No publication date found";

      // Выводим информацию
      console.log("Title:", title);
      console.log("Abstract:", abstract);
      console.log("Authors:", authors);
      console.log("Publication Date:", pubDate);

      // Вывод MeSH терминов
      console.log("MeSH Terms:", meshTerms);
    }
    catch (error) {
      console.log('Failed to fetch article: ', error)
    }
  }
  useEffect(() => { getAllInfo() }, [])
  return (
    <div className='page_wrapper'>
      <h1 className='article_title'>{data?.title}</h1>
      <div className='categories_div'>{data?.categories?.map((category, i) => <p className='article_category'>{category}</p>)}</div>
      <p className='article_text'>{data?.text}</p>
    </div>
  )
}
