import React, { useEffect, useState } from 'react'
import './ArticleCard.css'
import { useNavigate } from 'react-router-dom'

export function ArticleCard({article}) {
    const navigate = useNavigate();

    const getArticleAbstract = async (articleId) => {
        const response = await fetch(`https://eutils.ncbi.nlm.nih.gov/entrez/eutils/efetch.fcgi?db=pubmed&id=${articleId}&retmode=xml`);
        const xmlText = await response.text();
    
        const parser = new DOMParser();
        const xmlDoc = parser.parseFromString(xmlText, "application/xml");
        const abstractText = xmlDoc.querySelector("AbstractText")?.textContent || "Аннотация недоступна";
    
        return abstractText;
    };
    const [articleData, setData] = useState({ })

    useEffect(()=>{
        const annotation = getArticleAbstract(article.uid);
        setData({title: article.title, annotation: annotation, src: './assets/registration_banner.png', uid: article.uid})
    },[article]);

    return (
        <div className='article_card_wrapper' onClick={()=>{ navigate('/article')}}>
            <div className='article_card_img_div'><img alt={articleData.title +'_img'} src={articleData.src}/></div>
            <div className='article_info'>
                <h1>{article.title}</h1>
                <p></p>
            </div>
        </div>
    )
}
